package com.sakurafish.common.lib.ui;import android.app.ProgressDialog;import android.content.Context;import android.os.Bundle;import android.view.View;import android.widget.TextView;public class GeneralProgressDialog extends ProgressDialog {    /**     * Messageのテキストサイズ(dip).     */    private final static int TEXT_SIZE = 20;    public GeneralProgressDialog(final Context context) {        super(context);    }    public GeneralProgressDialog(final Context context, final int theme) {        super(context, theme);    }    @Override    protected void onCreate(final Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        final View message = findViewById(android.R.id.message);        if (message != null && message instanceof TextView) {            ((TextView) message).setTextSize(TEXT_SIZE);        }    }}